const getAllBlog = async ({ page = 1 }) => {
  try {
    let get = axios.get(
      `http://estatemanage.laptrinhjavawebsoftware.com/api-admin-blogs?page=${page}`
    );

    return (await get).data;
  } catch (error) {
    return [];
  }
};
const getBlogById = (id) => {
  return data.filter((el) => el.id === id)[0];
};

